mapper.select:
SELECT *
FROM t_user
WHERE t_user.f_machine_id = #{machineId} AND t_user.f_user_id = #{userId}

mapper.update:
UPDATE t_user
SET f_user_status          = #{status}, f_user_cup_size = #{cupSize},
    f_user_fav_temperature = #{favTemperature}
WHERE f_user_id = #{userId} and f_machine_id = #{machineId}
